import React, { useState, useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import LoginButton from "./LoginButton";

export default function Dashboard() {
  const { isAuthenticated, user } = useAuth0();
  return (
    <>
      {isAuthenticated ? (
        <DashboardContent user={user} />
      ) : (
        <div>
          Please log in for content managent
          <LoginButton />
        </div>
      )}
    </>
  );
}

export function DashboardContent() {
  const [data, setData] = useState([]);
  const FetchData = () => {
    fetch("http://localhost:4000/api/emojis")
      .then((res) => res.json())
      .then((data) => setData(data));
  };

  useEffect(() => {
    FetchData();
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const data = {
      name: formData.get("name"),
      emoji: formData.get("emoji"),
    };
    fetch("http://localhost:4000/api/emojis", {
      method: "POST",
      body: JSON.stringify(data),
      headers: {
        "Content-Type": "application/json",
      },
    }).then(FetchData);
  };

  const handleDelete = (id) => {
    fetch(`http://localhost:4000/api/emojis/${id}`, {
      method: "DELETE",
    }).then(FetchData);
  };
  return (
    <div className="container mx-auto flex flex-col gap-24 mt-16 px-4 md:px-0">
      <form onSubmit={handleSubmit}>
        <div className="flex flex-col space-y-8">
          <div>
            <label className="text-gray-700 text-sm mb-2">
              Kelime İngilizce
            </label>
            <input
              name="name"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              type="text"
            />
          </div>
          <div>
            <label className="text-gray-700 text-sm mb-2">Kelime Türkçe</label>
            <input
              name="emoji"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              type="text"
            />
          </div>
          <div>
            <label className="text-gray-700 text-sm mb-2">
              Kelime Türkçe Açıklama
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              type="text"
            />
          </div>
          <div>
            <label className="text-gray-700 text-sm mb-2">
              Kelime İngilizce Açıklama
            </label>
            <input
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              type="text"
            />
          </div>
          <button
            className="bg-yellow-400 text-lg text-white px-4 py-2 rounded"
            type="submit"
          >
            Ekle
          </button>
        </div>
      </form>

      <table class="min-w-full border-collapse block md:table">
        <thead class="block md:table-header-group">
          <tr class="border border-grey-500 md:border-none block md:table-row absolute -top-full md:top-auto -left-full md:left-auto  md:relative ">
            <th class="bg-yellow-500 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">
              Kelime Türkçe
            </th>
            <th class="bg-yellow-500 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">
              Kelime İngilizce
            </th>
            <th class="bg-yellow-500 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">
              Kelime Türkçe Açıklama
            </th>
            <th class="bg-yellow-500 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">
              Kelime İngilizce Açıklama
            </th>
            <th class="bg-yellow-500 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell"></th>
          </tr>
        </thead>
        <tbody class="block md:table-row-group">
          {data.map((item) => (
            <tr class="bg-gray-300 border border-grey-500 md:border-none block md:table-row">
              <td class="p-2 md:border md:border-grey-500 text-left block md:table-cell">
                <span class="inline-block w-1/3 md:hidden font-bold">Name</span>
                {item.name}
              </td>
              <td class="p-2 md:border md:border-grey-500 text-left block md:table-cell">
                <span class="inline-block w-1/3 md:hidden ">{item.emoji}</span>
                lillie
              </td>
              <td class="p-2 md:border md:border-grey-500 text-left block md:table-cell">
                <span class="inline-block w-1/3 md:hidden font-bold">
                  Email Address
                </span>
                {item.emoji}
              </td>
              <td class="p-2 md:border md:border-grey-500 text-left block md:table-cell">
                <span class="inline-block w-1/3 md:hidden font-bold">
                  Mobile
                </span>
                {item._id}
              </td>
              <td class="p-2 md:border md:border-grey-500 text-left block md:table-cell">
                <span class="inline-block w-1/3 md:hidden font-bold">
                  Actions
                </span>
                <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 border border-blue-500 rounded">
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(item._id)}
                  className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 border border-red-500 rounded"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
